package com.example.root.dictionaryproject;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.TextView;public class DetailActivity extends AppCompatActivity {    TextView kata;    TextView arti;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        kata    = (TextView)findViewById(R.id.kata);        arti    = (TextView)findViewById(R.id.keterangan);        kata.setText(getIntent().getStringExtra("kata"));        arti.setText(getIntent().getStringExtra("arti"));    }}