package com.example.root.dictionaryproject.helper;import android.content.Context;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.example.root.dictionaryproject.DetailActivity;import com.example.root.dictionaryproject.R;import java.util.ArrayList;public class MyAdapter extends RecyclerView.Adapter<MyAdapter.MyHolder>{    private Context context;    private LayoutInflater inflater;    private ArrayList<MyModel> models;    public MyAdapter(Context context) {        this.context = context;        inflater     = (LayoutInflater)context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);    }    public void addItem(ArrayList<MyModel> models){        this.models = models;        notifyDataSetChanged();    }    public int getItemViewType(int position){        return 0;    }    public long getItemId(int position){        return position;    }    @NonNull    @Override    public MyAdapter.MyHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.list_item, viewGroup, false);        return new MyHolder(view);    }    @Override    public void onBindViewHolder(@NonNull MyAdapter.MyHolder myHolder, int i) {        final String kata = models.get(i).getKata();        final String arti = models.get(i).getArti();        myHolder.word.setText(kata);        myHolder.meaning.setText(arti);        myHolder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(context, DetailActivity.class);                intent.putExtra("kata", kata);                intent.putExtra("arti", arti);                context.startActivity(intent);            }        });    }    @Override    public int getItemCount() {        return models.size();    }    public class MyHolder extends RecyclerView.ViewHolder {        TextView word;        TextView meaning;        public MyHolder(@NonNull View itemView) {            super(itemView);            word    = (TextView)itemView.findViewById(R.id.word);            meaning = (TextView)itemView.findViewById(R.id.meaning);        }    }}