package com.example.root.dictionaryproject.helper;import android.os.Parcel;import android.os.Parcelable;public class MyModel implements Parcelable{    private int id;    private String kata;    private String arti;    public MyModel(){    }    public MyModel(String kata, String arti) {        this.kata = kata;        this.arti = arti;    }    public MyModel(int id, String kata, String arti) {        this.id = id;        this.kata = kata;        this.arti = arti;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getKata() {        return kata;    }    public void setKata(String kata) {        this.kata = kata;    }    public String getArti() {        return arti;    }    public void setArti(String arti) {        this.arti = arti;    }    protected MyModel(Parcel in) {        this.id     = in.readInt();        this.kata   = in.readString();        this.arti   = in.readString();    }    public static final Creator<MyModel> CREATOR = new Creator<MyModel>() {        @Override        public MyModel createFromParcel(Parcel in) {            return new MyModel(in);        }        @Override        public MyModel[] newArray(int size) {            return new MyModel[size];        }    };    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel parcel, int i) {        parcel.writeInt(id);        parcel.writeString(kata);        parcel.writeString(arti);    }}